import axios from 'axios'
import React, { useEffect, useState } from 'react'
const Cart = () => {
  const [cartItem, setCartItem] = useState()
  useEffect(() => {
    axios.get('http://localhost:3003/getcartitems').then(res => setCartItem([...res.data])).catch(err => console.log(err))
  }, [])
  // console.log(cartItem)
  return (
    <>
       {cartItem?.length===0 &&  <div>no item in cart </div>} 
       {
         ! cartItem && 
         <div className='flex justify-center items-center'>
           {/* <div className='transform transition hover:rotate-180 duration-500 '>Loading</div> */}
           <div className='animate-spin rounded-full w-20 h-20 bg-blue-300 text-center'>
             <div className='h-8 w-8 bg-black'></div>
           </div>
           </div>

         
       }
       <div>

        { cartItem && cartItem?.length!=0 && cartItem.map(item =>
          <div>
            {item.Dname}
          </div>
        )}

      </div>

    </>
  )
}

export default Cart
