import axios from 'axios'
import React, { useEffect, useState } from 'react'
const Cart = () => {
  const [cartItem, setCartItem] = useState()
  useEffect(() => {
    axios.get('http://localhost:3003/getcartitems').then(res => setCartItem([...res.data])).catch(err => console.log(err))
  }, [])
  // console.log(cartItem)
  return (
    <>

    <div>
      <div className='flex justify-center items-cennter'>
      <img src="../images/shopping-cart.png" alt="" className='h-20' />
      <h2>Cart</h2>
      </div>
    </div>

      {cartItem?.length === 0 &&
        <div>
          no item in cart
        </div>}
      {
        !cartItem &&
        <div className='flex justify-center items-center'>
          
          <img src="../images/loading-buffering.gif" alt="" />

        </div>



      }
      <div>

        {cartItem && cartItem?.length != 0 && cartItem.map(item =>
          <div>
            {item.Dname}
          </div>
        )}

      </div>

    </>
  )
}

export default Cart
