import React from 'react'
import { useState } from 'react'

function Data() {

  const [specInput, setSpecInput] = useState([
    { specs: "" }
    

  ])
  console.log(specInput)

  const specInputAdd = () => {
    setSpecInput([...specInput, {specs:""}])
  }

  const specInputRemove = (index) => {
    const specs = [...specInput];
    specs.splice(index,1);
    setSpecInput(specs);
  }

  const handleSpecsChange = (e,index)=>{

    const {name,value} = e.target
    const specs = [...specInput]
    specs[index][name]=value;
    setSpecInput(specs)
  }

  return (
    <>
      <form action="">
        <div className='flex flex-col justify-center items-center'>
          <h2 className='text-3xl'>Add Specification</h2>
          {specInput.map((specData, index) => (
            <div key={index} className='my-2 '>
              <div className='flex my-2'>
                <input type="text" className='outline-none border rounded p-1' name='specs' value={specData.specs} onChange={(e)=>(handleSpecsChange(e,index))} />
                {specInput.length > 1 &&
                  <button  className='border rounded p-1 bg-red-500 text-white' onClick={()=>(specInputRemove(index))} >Remove</button>
                }
              </div>
              {specInput.length - 1 === index && specInput.length < 8 &&
                <button className='border rounded p-1 bg-green-500 text-white' onClick={specInputAdd}>add More</button>}
            </div>
          ))}

        </div>
      </form>
    </>

  )
}

export default Data
