import React, { useEffect, useState } from 'react'
import axios from 'axios'
import phones from "../images/phones.png"
// import phone from "../images/phone.png"


function Mobiles() {

    const [data, setData] = useState()

    useEffect(
        () =>
            axios.get("http://localhost:3003/data")
                .then((res) => {
                    setData(res.data);
                    console.log(res.data);
                })
                .catch((err) => {
                    //   console.log(err);

                }), []
    );


    return (
        <div className='bg-gray-300'>
            <div>

            </div>

            {data?.map((e) => (
                <div className=" flex   mx-32">

                    <div className="border w-3/12 flex justify-center items-center bg-white rounded-xl m-4 p-4 shadow">
                        <img className="h-48" src={e.img} alt="" />
                    </div>
                    <div className="border w-6/12 bg-white rounded-xl m-4 p-4 shadow">
                        <h2 className="text-3xl font-semibold">{e.Dname}</h2>
                        <div>
                            {e.spec.map((specs) => (
                                <h2>{specs}</h2>
                            ))}
                        </div>
                        <div className='text'>
                        </div>
                    </div>
                    <div className=" flex flex-col justify-between w-3/12 bg-white rounded-xl m-4 p-4 shadow">
                        <h2 className="text-3xl font-semibold">{e.price}â‚¹</h2>
                        <div className='flex justify-around items-center'>
                            <button className='bg-blue-400 p-2 rounded'>Add To Cart</button>
                            <button className='bg-yellow-300 p-2 rounded'>Buy Now</button>
                        </div>
                    </div>
                </div>
            ))}

        </div>
    )
}

export default Mobiles
